jQuery(document).ready(function () {
	jQuery("#post-video-url").hide(),
		jQuery("#post-format-video").is(":checked") &&
			jQuery("#post-video-url").show(),
		jQuery('input[name="post_format"]').change(function () {
			jQuery("#post-video-url").hide();
		}),
		jQuery("input#post-format-video").change(function () {
			jQuery("#post-video-url").show();
		});
}),
	jQuery(window).on("load", function () {
		var e = jQuery(
			'select[id^="post-format-selector"] option:selected'
		).attr("value");
		e &&
			("video" === e
				? jQuery("#post-video-url").show()
				: jQuery("#post-video-url").hide(),
			jQuery(document).on(
				"change",
				'select[id*="post-format"]',
				function () {
					"video" === this.value
						? jQuery("#post-video-url").show()
						: jQuery("#post-video-url").hide();
				}
			));
	}),
	jQuery("#page-settings-tabs-wrapper").tabs();
